document.addEventListener("DOMContentLoaded",function(){Dropzone.autoDiscover=!1;var e=new Dropzone("#myDropzone",{url:"https://httpbin.org/post",maxFilesize:5,acceptedFiles:"image/*",addRemoveLinks:!0,autoProcessQueue:!0});e.on("addedfile",function(e){console.log("File added: "+e.name)}),e.on("removedfile",function(e){console.log("File removed: "+e.name)}),e.on("success",function(e,o){console.log("File uploaded successfully:",o)})});